import React , {useState}from 'react'
import Joi from 'joi';

export default function Register() {
  let [user,setUser]=useState({
    Name:"",
    Email:"",
    Password:"",
    Repeat_Password:"",
    Phone_Number:""
  })
  // let isShownRepeated;
  // let isShown;
  function submitFormData(e){
   e.preventDefault();
   let validationResult=validateForm();
   console.log(validationResult);
  }
  function validateForm(){ 
const schema=Joi.object({
  Name:Joi.string().alphanum().required().min(3).max(25),
  Email:Joi.string().required().email({tlds:{allow: true}}),
  Password:Joi.string().required().pattern(new RegExp('^[a-z][0-9]{3}$')),
  Repeat_Password:Joi.string().required().pattern(new RegExp('^[a-z][0-9]{3}$')),
  Phone_Number:Joi.number().required().length(12).pattern(/^[0-9]+$/)
});
 return schema.validate(user,{abortEarly:false});

  }
  function getFormValue(e){
     let myUser={...user};
     myUser[e.target.name]=e.target.value;
     setUser(myUser);
     console.log(myUser);
  }
  return (
    
    <div className={"container w-50 my-5 m-auto"}>
      <div>
      <div className={"Header my-5 text-center"}>
        <h1>Welcome to the Family</h1>
      </div>
<form onSubmit={submitFormData}>
      <div className="regBox">
        {/*  Name*/}
        <div className="input-group mb-3 ">
          <span className="input-group-text">Name</span>
          <input
            onChange={getFormValue}
            type="text"
            className="form-control"
            placeholder="Enter your Full Name"
            name="Name" />
        </div>

        {/*    email*/}
        <div className="input-group mb-3 ">
          <span className="input-group-text">Email</span>
          <input
              onChange={getFormValue}
              type="text"
              className="form-control"
              placeholder="Enter your email address"
              name='Email'/>
        </div>

        {/*    Password*/}
        <div className="input-group mb-3">
          <span className="input-group-text">Password</span>
          <input
              onChange={getFormValue}
              type="text"
              // type={isShown ? "text" : "password"}
              className="form-control"
              placeholder="Enter your password"
              name='Password'
          />
        </div>

        {/*    Repeat password*/}
        <div className="input-group mb-3">
          <span className="input-group-text">Repeat Password</span>
          <input
           onChange={getFormValue}
              type="text"
              // type={isShownRepeated ? "text" : "password"}
              className="form-control"
              placeholder="Repeat your password"
              name='Repeat_Password'
          />
        </div>

        {/*    Phone Number*/}
        <div className="input-group mb-3 ">
          <span className="input-group-text">Phone Number</span>
          <input
           onChange={getFormValue}
              type="Number"
              className="form-control"
              placeholder="Enter your Phone Number"
              name='Phone_Number'/>
        </div>

        {/* <div className={"justify-content-between"}>
          {/* <div className={"btn btn-primary m-lg-3"}>Sign Up</div> */}
          {/* <button type="button" className="btn btn-secondary m-lg-3">Sing Up</button> */}
          {/* <button type="button" className="btn btn-secondary m-lg-3">Become a partner</button>
          <button type="button" className="btn btn-secondary m-lg-3">Sign in</button> */} 
        </div>
      {/* </div> */}
      <button className="btn btn-info px-5">Sing Up</button>
      </form>
      
      </div>
    </div>
    
  );
}

